        /* ---- Hero ---- */
        .ed-hero {
          min-height: 100vh;
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
          text-align: center;
          padding: 6rem 2rem 4rem;
          position: relative;
          overflow: hidden;
        }

        .ed-hero-name {
          font-family: 'Playfair Display', serif;
          font-weight: 800;
          font-size: clamp(3rem, 9vw, 10rem);
          line-height: 0.92;
          letter-spacing: -0.025em;
          color: var(--ed-text);
          margin: 0;
          opacity: 0;
          animation: heroNameIn 1.2s cubic-bezier(0.25, 0.1, 0.25, 1) 0.2s forwards;
        }

        .ed-hero-subtitle {
          font-family: 'DM Sans', sans-serif;
          font-size: clamp(0.9rem, 1.5vw, 1.25rem);
          font-weight: 400;
          letter-spacing: 0.2em;
          text-transform: uppercase;
          color: var(--ed-secondary);
          margin-top: 1.5rem;
          opacity: 0;
          animation: heroFadeUp 0.8s ease 0.8s forwards;
        }

        .ed-hero-rule {
          width: 60px;
          height: 1px;
          background: var(--ed-accent);
          border: none;
          margin: 2rem auto 0;
          opacity: 0;
          animation: heroFadeUp 0.8s ease 1.1s forwards;
        }

        .ed-hero-location {
          font-family: 'DM Sans', sans-serif;
          font-size: 0.85rem;
          color: var(--ed-secondary);
          margin-top: 1.2rem;
          font-weight: 400;
          letter-spacing: 0.05em;
          opacity: 0;
          animation: heroFadeUp 0.8s ease 1.3s forwards;
        }

        .ed-hero-scroll {
