}

export default function EditorialPage() {
  const mainRef = useScrollReveal();

  return (
    <>
      <Head>
        <title>Jean du Plessis | Engineering Leader</title>
        <meta
          name="description"
          content="Jean du Plessis — Engineering Leader, Director of Engineering. Personal portfolio and management README."
        />
      </Head>

      <style>{`
        @import url('https://fonts.googleapis.com/css2?family=Playfair+Display:ital,wght@0,400;0,500;0,600;0,700;0,800;0,900;1,400;1,500;1,600;1,700&family=DM+Sans:ital,opsz,wght@0,9..40,300;0,9..40,400;0,9..40,500;0,9..40,600;0,9..40,700;1,9..40,300;1,9..40,400;1,9..40,500&display=swap');

        :root {
          --ed-bg: #F5F0EB;
          --ed-text: #2C2C2C;
          --ed-accent: #C45D3E;
          --ed-secondary: #8B8178;
          --ed-rule: #D4CBC2;
          --ed-cream-light: #FAF7F4;
        }

        .editorial-page {
          font-family: 'DM Sans', sans-serif;
          background-color: var(--ed-bg);
          color: var(--ed-text);
          -webkit-font-smoothing: antialiased;
          -moz-osx-font-smoothing: grayscale;
        }

        .editorial-page * {
          box-sizing: border-box;
        }

        .font-display {
          font-family: 'Playfair Display', Georgia, 'Times New Roman', serif;
        }

        .font-body {
          font-family: 'DM Sans', system-ui, sans-serif;
        }

        /* ---- Navigation ---- */
        .ed-nav {
          position: sticky;
          top: 0;
          z-index: 100;
          background: rgba(245, 240, 235, 0.92);
          backdrop-filter: blur(12px);
          -webkit-backdrop-filter: blur(12px);
          border-bottom: 1px solid var(--ed-rule);
          transition: box-shadow 0.3s ease;
        }

        .ed-nav-inner {
          max-width: 1200px;
          margin: 0 auto;
          padding: 0 2rem;
          display: flex;
          align-items: center;
          justify-content: space-between;
          height: 60px;
        }

        .ed-nav-logo {
          font-family: 'Playfair Display', serif;
          font-weight: 700;
          font-size: 1.1rem;
          color: var(--ed-text);
          text-decoration: none;
          letter-spacing: 0.01em;
        }

        .ed-nav-links {
          display: flex;
          gap: 2rem;
          list-style: none;
          margin: 0;
          padding: 0;
        }

        .ed-nav-link {
          font-family: 'DM Sans', sans-serif;
          font-size: 0.75rem;
          font-weight: 500;
          letter-spacing: 0.14em;
          text-transform: uppercase;
          color: var(--ed-secondary);
          text-decoration: none;
          position: relative;
          transition: color 0.3s ease;
        }

        .ed-nav-link::after {
          content: '';
          position: absolute;
          bottom: -2px;
          left: 0;
          width: 0;
          height: 1px;
          background: var(--ed-accent);
          transition: width 0.35s cubic-bezier(0.25, 0.1, 0.25, 1);
        }

        .ed-nav-link:hover {
          color: var(--ed-text);
        }

        .ed-nav-link:hover::after {
          width: 100%;
        }

        /* ---- Hero ---- */
        .ed-hero {
          min-height: 100vh;
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
          text-align: center;
          padding: 6rem 2rem 4rem;
          position: relative;
          overflow: hidden;
        }

        .ed-hero-name {
          font-family: 'Playfair Display', serif;
          font-weight: 800;
          font-size: clamp(3rem, 9vw, 10rem);
          line-height: 0.92;
          letter-spacing: -0.025em;
          color: var(--ed-text);
          margin: 0;
          opacity: 0;
          animation: heroNameIn 1.2s cubic-bezier(0.25, 0.1, 0.25, 1) 0.2s forwards;
        }

        .ed-hero-subtitle {
          font-family: 'DM Sans', sans-serif;
          font-size: clamp(0.9rem, 1.5vw, 1.25rem);
          font-weight: 400;
          letter-spacing: 0.2em;
          text-transform: uppercase;
          color: var(--ed-secondary);
          margin-top: 1.5rem;
          opacity: 0;
          animation: heroFadeUp 0.8s ease 0.8s forwards;
        }

        .ed-hero-rule {
          width: 60px;
          height: 1px;
          background: var(--ed-accent);
          border: none;
          margin: 2rem auto 0;
          opacity: 0;
          animation: heroFadeUp 0.8s ease 1.1s forwards;
        }

        .ed-hero-location {
          font-family: 'DM Sans', sans-serif;
          font-size: 0.85rem;
          color: var(--ed-secondary);
          margin-top: 1.2rem;
          font-weight: 400;
          letter-spacing: 0.05em;
          opacity: 0;
          animation: heroFadeUp 0.8s ease 1.3s forwards;
        }

        .ed-hero-scroll {
          position: absolute;
          bottom: 2.5rem;
          left: 50%;
          transform: translateX(-50%);
          display: flex;
          flex-direction: column;
          align-items: center;
          gap: 0.5rem;
          opacity: 0;
          animation: heroFadeUp 0.8s ease 1.6s forwards;
        }

        .ed-hero-scroll span {
          font-size: 0.65rem;
          letter-spacing: 0.2em;
          text-transform: uppercase;
          color: var(--ed-secondary);
        }

        .ed-scroll-line {
          width: 1px;
          height: 40px;
          background: var(--ed-rule);
          position: relative;
          overflow: hidden;
        }

        .ed-scroll-line::after {
          content: '';
          position: absolute;
          top: -100%;
          left: 0;
          width: 1px;
          height: 100%;
          background: var(--ed-accent);
          animation: scrollPulse 2s ease infinite;
        }

        /* ---- Sections ---- */
        .ed-section {
          max-width: 1200px;
          margin: 0 auto;
          padding: 6rem 2rem;
        }

        .ed-section-divider {
          max-width: 1200px;
          margin: 0 auto;
          padding: 0 2rem;
        }

        .ed-section-divider hr {
          border: none;
          height: 1px;
          background: var(--ed-rule);
          margin: 0;
        }

        .ed-section-header {
          display: flex;
          align-items: baseline;
          gap: 1.5rem;
          margin-bottom: 3.5rem;
        }

        .ed-section-number {
          font-family: 'Playfair Display', serif;
          font-size: clamp(3rem, 5vw, 5rem);
          font-weight: 300;
          color: var(--ed-accent);
          opacity: 0.25;
          line-height: 1;
          flex-shrink: 0;
        }

        .ed-section-title {
          font-family: 'Playfair Display', serif;
          font-size: clamp(1.8rem, 3vw, 2.8rem);
          font-weight: 700;
          line-height: 1.15;
          margin: 0;
          letter-spacing: -0.015em;
        }

        /* ---- Two Column Grid ---- */
        .ed-grid {
          display: grid;
          grid-template-columns: 1fr;
          gap: 3rem;
        }

        @media (min-width: 768px) {
          .ed-grid {
            grid-template-columns: 5fr 7fr;
            gap: 4rem;
          }
        }

        .ed-grid-wide {
          display: grid;
          grid-template-columns: 1fr;
          gap: 3rem;
        }

        @media (min-width: 768px) {
          .ed-grid-wide {
            grid-template-columns: 7fr 5fr;
            gap: 4rem;
          }
        }

        /* ---- About ---- */
        .ed-profile-img {
          border-radius: 4px;
          filter: sepia(15%) saturate(85%) brightness(102%);
          transition: filter 0.5s ease;
        }

        .ed-profile-img:hover {
          filter: sepia(0%) saturate(100%) brightness(100%);
        }

        .ed-about-text {
          font-size: 1.05rem;
          line-height: 1.85;
          color: var(--ed-text);
        }

        .ed-about-text p:first-of-type::first-letter {
          font-family: 'Playfair Display', serif;
          font-size: 3.6em;
          float: left;
          line-height: 0.8;
          margin-right: 0.08em;
          margin-top: 0.05em;
          color: var(--ed-accent);
          font-weight: 700;
        }

        .ed-about-text p {
          margin: 0 0 1.4rem;
        }

        .ed-family-img {
          border-radius: 4px;
          margin-top: 1.5rem;
        }

        .ed-img-caption {
          font-size: 0.78rem;
          color: var(--ed-secondary);
          margin-top: 0.6rem;
          font-style: italic;
          letter-spacing: 0.01em;
        }

        /* ---- Pull Quote ---- */
        .ed-pull-quote {
          padding: 3rem 0;
          max-width: 1200px;
          margin: 0 auto;
          text-align: center;
        }

        .ed-pull-quote blockquote {
          font-family: 'Playfair Display', serif;
          font-size: clamp(1.5rem, 2.8vw, 2.4rem);
          font-weight: 500;
          font-style: italic;
          line-height: 1.4;
          color: var(--ed-text);
          margin: 0;
          padding: 0 2rem;
          position: relative;
        }

        .ed-pull-quote blockquote::before {
          content: '\\201C';
          font-family: 'Playfair Display', serif;
          font-size: 5rem;
          color: var(--ed-accent);
          opacity: 0.35;
          position: absolute;
          top: -1.8rem;
          left: 0;
          line-height: 1;
        }

        /* ---- README ---- */
        .ed-readme-grid {
          display: grid;
          grid-template-columns: 1fr;
          gap: 2rem;
        }

        @media (min-width: 768px) {
          .ed-readme-grid {
            grid-template-columns: 1fr 1fr;
            gap: 2.5rem 3rem;
          }
        }

        .ed-readme-card {
