        @import url('https://fonts.googleapis.com/css2?family=Lora:ital,wght@0,400;0,500;0,600;0,700;1,400;1,500;1,600;1,700&family=Karla:ital,wght@0,300;0,400;0,500;0,600;0,700;1,300;1,400;1,500&display=swap');

        :root {
          --ed-bg: #1C1917;
          --ed-text: #F5F0E8;
          --ed-accent: #4ECDC4;
          --ed-secondary: #9C8F80;
          --ed-rule: #33302B;
          --ed-cream-light: #22201C;
        }

        .editorial-page {
          font-family: 'Karla', sans-serif;
          background-color: var(--ed-bg);
          color: var(--ed-text);
          -webkit-font-smoothing: antialiased;
          -moz-osx-font-smoothing: grayscale;
        }

        .editorial-page * {
          box-sizing: border-box;
        }

        .font-display {
          font-family: 'Lora', Georgia, 'Times New Roman', serif;
        }

        .font-body {
          font-family: 'Karla', system-ui, sans-serif;
        }

        /* ---- Navigation ---- */
        .ed-nav {
          position: sticky;
          top: 0;
          z-index: 100;
          background: rgba(28, 25, 23, 0.92);
          backdrop-filter: blur(12px);
          -webkit-backdrop-filter: blur(12px);
          border-bottom: 1px solid var(--ed-rule);
          transition: box-shadow 0.3s ease;
        }

        .ed-nav-inner {
          max-width: 1200px;
          margin: 0 auto;
          padding: 0 2rem;
          display: flex;
          align-items: center;
          justify-content: space-between;
          height: 60px;
        }

        .ed-nav-logo {
          font-family: 'Lora', serif;
          font-weight: 700;
          font-size: 1.1rem;
          color: var(--ed-text);
          text-decoration: none;
          letter-spacing: 0.01em;
        }

        .ed-nav-links {
          display: flex;
          gap: 2rem;
          list-style: none;
          margin: 0;
          padding: 0;
        }

        .ed-nav-link {
          font-family: 'Karla', sans-serif;
          font-size: 0.75rem;
          font-weight: 500;
          letter-spacing: 0.14em;
          text-transform: uppercase;
          color: var(--ed-secondary);
          text-decoration: none;
