  55→function useScrollReveal() {
  56→  const ref = useRef(null);
  57→
  58→  useEffect(() => {
  59→    const observer = new IntersectionObserver(
  60→      (entries) => {
  61→        entries.forEach((entry) => {
  62→          if (entry.isIntersecting) {
  63→            entry.target.classList.add("editorial-visible");
  64→            observer.unobserve(entry.target);
  65→          }
  66→        });
  67→      },
  68→      { threshold: 0.12, rootMargin: "0px 0px -40px 0px" }
  69→    );
  70→
  71→    const el = ref.current;
  72→    if (el) {
  73→      const targets = el.querySelectorAll(".editorial-reveal");
  74→      targets.forEach((t) => observer.observe(t));
  75→    }
  76→
  77→    return () => observer.disconnect();
  78→  }, []);
  79→
  80→  return ref;
  81→}
